import { Component, ElementRef, HostListener, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DraggableElement } from '../../models/draggable-element.model';

@Component({
  selector: 'app-draggable-element',
  templateUrl: './draggable-element.component.html',
  styleUrls: ['./draggable-element.component.css'],
})

// Sostituisci con l'URL del tuo backend API


export class DraggableElementComponent implements OnInit {
  private isDragging = false;
  private draggedElement: HTMLElement | null = null;
  private apiUrl = 'https://localhost:7164';

  @ViewChild('element1') element1: ElementRef;
  @ViewChild('element2') element2: ElementRef;
  @ViewChild('element3') element3: ElementRef;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.getCoordinatesByName('element1');
  }
  @HostListener('document:mousemove', ['$event'])
  onDrag(event: MouseEvent): void {
    if (this.isDragging && this.draggedElement) {
      this.draggedElement.style.left = event.clientX + 'px';
      this.draggedElement.style.top = event.clientY + 'px';
    }
  }

  @HostListener('document:mouseup', ['$event'])
  onDragEnd(event: MouseEvent): void {
    if (this.isDragging && this.draggedElement) {
      this.isDragging = false;

      // Ottieni le coordinate dell'elemento trascinato come stringhe
      const topValue = this.draggedElement.style.top;
      const leftValue = this.draggedElement.style.left;

      // Rimuovi "px" dai valori e converti in numeri
      const topNumeric = parseInt(topValue, 10);
      const leftNumeric = parseInt(leftValue, 10);

      const coordinates = {
        name: this.draggedElement.id,
        x: topNumeric,
        y: leftNumeric,
      };

      // Invia le coordinate al backend tramite una chiamata API
      this.saveCoordinates(coordinates);

      this.draggedElement = null;
    }
  }


  onDragStart(event: MouseEvent): void {
    this.isDragging = true;
    this.draggedElement = event.target as HTMLElement;
  }

  private saveCoordinates(coordinates: any): void {


    this.http.post(`${this.apiUrl}/api/SaveCoordinate`, coordinates)
      .subscribe(
        (response) => {
          console.log('Coordinate salvate:', coordinates);
        },
        (error) => {
          console.error('Errore durante il salvataggio delle coordinate:', error);
          // Puoi gestire l'errore qui, ad esempio, mostrare un messaggio all'utente o effettuare altre azioni
        }
      );
  }

  private getCoordinatesByName(elementName: string): void {
    this.http.get<any>(`${this.apiUrl}/api/GetCoordinate?elementName=${elementName}`)
      .subscribe((response: any) => {
        const elements: DraggableElement[] = response;

        const element = elements.find(e => e.name === elementName);

        if (element) {
          const elementTop = element.y; // Assumendo che Y sia la coordinata verticale
          const elementLeft = element.x; // Assumendo che X sia la coordinata orizzontale

          // Aggiorna la posizione dell'elemento come desiderato
          // Ad esempio, assegnando le coordinate all'elemento HTML utilizzando ElementRef
          const elementRef = this.getElementRefByName(elementName);
          if (elementRef) {
            elementRef.nativeElement.style.top = `${elementTop}px`;
            elementRef.nativeElement.style.left = `${elementLeft}px`;
          }
        }
      });
  }

  private getElementRefByName(elementName: string): ElementRef | null {
    switch (elementName) {
      case 'element1':
        return this.element1;
      case 'element2':
        return this.element2;
      case 'element3':
        return this.element3;
      default:
        return null;
    }
  }




}
