import { Component, ElementRef, HostListener } from '@angular/core';

@Component({
  selector: 'app-draggable-element',
  templateUrl: './draggable-element.component.html',
  styleUrls: ['./draggable-element.component.css']
})
export class DraggableElementComponent {
  private isDragging = false;
  private initialX: number | undefined;
  private initialY: number | undefined;

  constructor(private elementRef: ElementRef) { }

  @HostListener('window:mousemove', ['$event'])
  onDrag(event: MouseEvent): void {
    if (this.isDragging) {
      const x = event.clientX - this.initialX;
      const y = event.clientY - this.initialY;
      this.elementRef.nativeElement.style.transform = `translate(${x}px, ${y}px)`;
    }
  }

  @HostListener('mousedown', ['$event'])
  onDragStart(event: MouseEvent): void {
    this.isDragging = true;
    this.initialX = event.clientX - this.elementRef.nativeElement.getBoundingClientRect().left;
    this.initialY = event.clientY - this.elementRef.nativeElement.getBoundingClientRect().top;
  }

  @HostListener('window:mouseup')
  onDragEnd(): void {
    if (this.isDragging) {
      this.isDragging = false;
      const element = this.elementRef.nativeElement;
      const x = element.getBoundingClientRect().left;
      const y = element.getBoundingClientRect().top;

      // Ora puoi salvare le coordinate (x, y) nel tuo database o in una variabile
      // e inviare i dati al server se necessario.
    }
  }
}
