import { Component } from '@angular/core';
import { CdkDragStart, CdkDragEnd } from '@angular/cdk/drag-drop';
import { ApiService } from '../../services/draggable-element.service';

@Component({
  selector: 'app-draggable-element',
  templateUrl: './draggable-element.component.html',
  styleUrls: ['./draggable-element.component.css'],
})
export class DraggableElementComponent {
  constructor(private apiService: ApiService) { }

  onDragStarted(event: CdkDragStart): void {
    // Invia le coordinate iniziali al backend quando inizia il trascinamento
    const element = event.source.element.nativeElement;
    this.sendCoordinates(element);
  }

  onDragEnded(event: CdkDragEnd): void {
    // Invia le coordinate finali al backend quando termina il trascinamento
    const element = event.source.element.nativeElement;
    this.sendCoordinates(element);
  }

  private sendCoordinates(element: HTMLElement): void {
    const coordinates = {
      top: element.style.top,
      left: element.style.left,
    };

    // Invia le coordinate al backend tramite un servizio API
    this.apiService.saveCoordinates(coordinates).subscribe((response) => {
      console.log('Coordinate salvate:', coordinates);
    });
  }
}
