import { Component, ElementRef, HostListener } from '@angular/core';

@Component({
  selector: 'app-draggable-element',
  templateUrl: './draggable-element.component.html',
  styleUrls: ['./draggable-element.component.css']
})

export class DraggableElementComponent {
  x = 0;
  y = 0;
  isDragging = false;
  initialMouseX = 0;
  initialMouseY = 0;
  initialElementX = 0;
  initialElementY = 0;

  constructor(private el: ElementRef) { }

  onDragStart(event: MouseEvent): void {
    this.isDragging = true;
    this.initialMouseX = event.clientX;
    this.initialMouseY = event.clientY;
    this.initialElementX = this.x;
    this.initialElementY = this.y;
  }

  @HostListener('document:mousemove', ['$event'])
  onDrag(event: MouseEvent): void {
    if (this.isDragging) {
      const deltaX = event.clientX - this.initialMouseX;
      const deltaY = event.clientY - this.initialMouseY;
      this.x = this.initialElementX + deltaX;
      this.y = this.initialElementY + deltaY;
    }
  }

  @HostListener('document:mouseup', ['$event'])
  onDragEnd(event: MouseEvent): void {
    this.isDragging = false;
  }
}




